    from roleInfo r 
    inner join roleInfoLocale rl on r.roleId=rl.roleId and languageId=language_Id
    where rl.roleName = role_Name and isSystemRole =1 and roleCategoryId =roleCategory_Id
	and r.isDeleted = 0
    


