 Description : Candidate Job Offers
*/

	
    
    select  
	   hireRequestCandidateId
	 , hrc.jobProfileHireRequestId
	 , hrc.candidateUserId
     , jp.jobProfileId
     , bs.businessSiteId
     , bs.businessId
     , siteName
     , bs.addressLine1
     , bs.addressLine2
     , jobSubClassification
     , employmentType
     , timeSheetFormat
