		update ChildAgencySite set Is_Checked =1 where Child_Id = ChildId
		SET SQL_SAFE_UPDATES=1
	END WHILE

	select group_concat(distinct Child_Id) as ChildAgencySite  from ChildAgencySite
	drop table ChildAgencySite
