   ,IN language_Id bigint(20)
)
BEGIN


/*
 Author: James
 Date  : 08/12/2014
 Description : Get location
*/

select cl.cityId
	, concat(cl.cityName, ', ',sl.stateName,', ',cnl.countryName ) location 
from city c 
inner join  cityLocale cl on c.cityId=cl.cityId and cl.languageId=language_Id
