		where  (shiftOfferId=shiftOffer_Id  or  exchangedShiftOfferId=shiftOffer_Id)
		and status=14  		    
        
        update candidateShiftOffer set status=28  ,updatedBy=created_By,updatedOn=utc_timeStamp()
