                 insert into userRoleAgencySite (userRoleId, agencySiteId,createdBy,createdOn,status)
                 select a.* from(
					select userRoleId,agencySiteId,created_By, utc_timestamp() ,2 from 
					agencySite 
					cross join
					(
						select distinct ur.userRoleId
						from scUser u 
