BEGIN

/*
 Author: Tijo Joseph
 Date  : 11/11/2014
 Description : Inserting and Updating Agency site users
*/

DECLARE userUniqueId nvarchar(75)
DECLARE StatusValue tinyint
DECLARE userRole_Id_new bigint
