                    updatedBy=user_Id,
                    updatedOn=utc_timestamp()
			WHERE beaconId = beacon_Id and businessId = business_Id
            
		IF ROW_COUNT() > 0 THEN
			select beacon_Id
		END IF
        
	END IF
    
END
