										,	documentType_Id
										,	file_Name
										,	fileMime_Type
										,	file_ETag
										,	file_Url
										,	created_By
										,	utc_timestamp() 
										,	file_Status
										,	0 )
							
                      if row_count()>0 then
							set jobProfileAttachment_Id=last_insert_id()
							select jobProfileAttachment_Id
                      end if                 
                else
						update jobProfileAttachment set 
									 	jobProfileId	=	coalesce(jobProfile_Id, jobProfileId)
