								where 	jobProfileAttachmentId	=	jobProfileAttachment_Id
					  if row_count()>0 then
							select jobProfileAttachment_Id
                      end if                   
                end if
        
        else
				update jobProfileAttachment  set 
								updatedBy	=	created_By
							,	updatedOn	=	utc_timestamp()
							,	isDeleted	=	1
						where 	jobProfileAttachmentId	=	jobProfileAttachment_Id
						if row_count()>0 then
							select jobProfileAttachment_Id
                      end if 
        end if
	



END
