				if old_status_Id=10 and offer_status in('PENDING','LASTMINUTESHIFTREQUEST','SICKREQUESTED','STAFFREQUESTED','NOTNOTIFIED') then
					call InsertCandidateShiftCancel(shiftOffer_Id,10,null,created_By,null,null) 
