							VALUES
							  (shiftOffer_Id,candidate_userId_Single,1,NULL,NULL,NULL,NULL,candidate_userId_Single,utc_timestamp(),candidate_userId_Single,utc_timestamp()) 
						END IF
					ELSE
						SET message='ALREADYEXISTS'
