	IF ROW_COUNT() > 0 THEN
		Select 1 as StatusValue
		delete from candidateEmergencyContact where candidateUserId =candidate_user_id
		call InsertUpdateUserContactDetails (candidate_user_id,contact_Details,is_Delete)
	end if
end if


